// app/layout.tsx

import {Providers} from "./providers";

export default function RootLayout({children}: { children: React.ReactNode }) {

  return (

    <html lang\="en" className\='dark'\>

      <body\>

        <Providers\>

          {children}

        </Providers\>

      </body\>

    </html\>

  );

}

> **Note**: NextUI automatically adds two themes, `light` and `dark`, to your application. You can use any of them by adding the `dark`/`light` class to the `html` tag. See the [theme docs](https://nextui.org/docs/customization/customize-theme) for more details.

